CREATE OR REPLACE FORCE EDITIONABLE VIEW "ORAMON_STATS"."ORAMON_TOP_SQLS_MAX_VERSION_COUNT" 
 ( "INSTANCE_NUMBER", "DBID", "B_SNAP_ID", "E_SNAP_ID", "B_SNAP_TIME", "E_SNAP_TIME", "INTERVAL_MIN", "DBTIMEMIN", "AAS", "HV", "ELAPSED_TIME_SEC", "EXECUTIONS", "ELAPSED_PER_EXEC_SEC", "PERCENT_OF_DBTIME_USED", "CPU_TIME_SEC", "CPU_TIME_MS_PER_EXEC", "PHYSICAL_READS", "PHYSICAL_READS_PER_EXECUTION", "BUFFER_GETS", "GETS_PER_EXECUTION", "ROWS_PROCESSED", "ROWS_PROCESSED_PER_EXECUTION", "PARSE_CALLS", "MAX_SHARABLE_MEM_KB", "LAST_SHARABLE_MEM_KB", "MAX_VERSION_COUNT", "LAST_VERSION_COUNT", "DELTA_VERSION_COUNT", "CLUSTER_WAIT_TIME_SEC", "CWT_PERCENT_OF_ELAPSED_TIME", "AVG_HARD_PARSE_TIME_MS", "MODULE", "SQL_TEXT", "EVENT_NAME", "INSTANCE_NAME", "HOST_NAME"
  )  AS 
  select tops."INSTANCE_NUMBER",tops."DBID",tops."B_SNAP_ID",tops."E_SNAP_ID",tops."B_SNAP_TIME",tops."E_SNAP_TIME",tops."INTERVAL_MIN",tops."DBTIMEMIN",tops."AAS",tops."HV",tops."ELAPSED_TIME_SEC",tops."EXECUTIONS",tops."ELAPSED_PER_EXEC_SEC",tops."PERCENT_OF_DBTIME_USED",tops."CPU_TIME_SEC",tops."CPU_TIME_MS_PER_EXEC",tops."PHYSICAL_READS",tops."PHYSICAL_READS_PER_EXECUTION",tops."BUFFER_GETS",tops."GETS_PER_EXECUTION",tops."ROWS_PROCESSED",tops."ROWS_PROCESSED_PER_EXECUTION",tops."PARSE_CALLS",tops."MAX_SHARABLE_MEM_KB",tops."LAST_SHARABLE_MEM_KB",tops."MAX_VERSION_COUNT",tops."LAST_VERSION_COUNT",tops."DELTA_VERSION_COUNT",tops."CLUSTER_WAIT_TIME_SEC",tops."CWT_PERCENT_OF_ELAPSED_TIME",tops."AVG_HARD_PARSE_TIME_MS",tops."MODULE",tops."SQL_TEXT"
       ,'max_version_count' as event_name
       ,(select get_instance_name from dual) instance_name
       ,di.host_name
from table(get_top_sqls(p_start_time => to_char(trunc(sysdate-30),'RRRR-MM-DD HH24:MI'), p_end_time => to_char(sysdate,'RRRR-MM-DD HH24:MI'),p_top_n_by_attribute => 'max_version_count')) tops
inner join perfstat.stats$database_instance di
on tops.dbid = di.dbid